import {React, useState, useEffect} from 'react'
import axios from 'axios'
import Favlists from './Favlists'
import Header from './Header'
import { useLocation } from 'react-router-dom';

export default function Fav() {

  const [list,setList] = useState(0);
  const [fav, setFav] =useState([]);
  // 1. useLocation 훅 취득
    const location = useLocation();
    // 2. location.state 에서 파라미터 취득
    let nowuser = location.state;
    console.log(nowuser);

    //console.log(nowuser.userId);


    useEffect(() => {

      axios.get(`api/v1/movie?userId=${nowuser.userId}`)
      .then(function (response) {
        //  console.log(response.data);
         let favList = response.data;
         console.log(favList);
         setFav(favList);
         setList(favList.length);
        }).catch(function (error) {
          alert('Fail to Load');
        });
    },[nowuser]);

    return (
    // <div class={list===0?"bg-[#598A9E]/[0.43] h-screen":" bg-[url('./images/mainBg.png')] bg-contain "}>
    <div class="bg-[#598A9E]/[0.43] min-h-screen">
      <Header user={nowuser}></Header>
      <span className="cursor-pointer text-[22pt] font-extrabold w-fit text-[#FFD177]" onClick={btnHome}>FavieList</span>
      <Favlists list={list} setList={setList} fav={fav} userId={nowuser.userId}></Favlists>
    </div>  
  )
}
