    const ElementRef = useRef(null);



  useEffect(() => {
      const Elementcount = ElementRef.current.childNodes.length;
      setList(Elementcount);
  },[])



  return (
    <div class="px-[10rem] pt-[5px] pb-[20px]">
        <ul ref={ElementRef}>
          {
            fav.map((item)=>(
              <Favlist></Favlist>
            ))
          }
        </ul>
    </div>


  )
}
