import React from 'react'
import axios from 'axios';

function List({item, data, setData}) {
    const btnHeart = (item) => {

        // const updatedata = data.map((item) => {
        //     if (item.title === title) {
        //         item.like = !item.like

        //     }
        //     return item;
        // });

        // setData(updatedata);
        // console.log(updatedata);
        let id = 1
        axios.post(`/api/v1/movie?userId=${id}`, {
            movieCd:"",
            movieCd : "ttnwje",
            movieNm: "agag",
            openDt : "gaga",
            audiAdd : "gaga",
            thumbnail : "https://ww.namu.la/s/bd52223e4d1f11fcc4c7f6506bf3321b26579bf118db6c1ca20492b9af4228a414edd25f1006baace220e4ca771288e0f38d6cbf253ae4e9d39aaf4b881600b0d65e518e7d94891837ee9a0c6a723aac0f4d2b7bf4a65b36bd1fe636aa49c632",
            rating : 5,
            comment : "좋았어요???",
            userRating : 4
        })
        .then(function (response) {
             console.log(response);  
        }).catch(function (error) {
            alert('Fail to Signup');
        }).then(function() {
            //always
        });
    }
    return (
    <li className="px-7 py-5 mb-[25px] bg-[#ffffff] rounded-[14px]">
        <div className="flex" id={item.movieCd}>
            <img src={item.poster} alt="Err" className=" mr-5 float-left w-[85px]"></img>
            <div className="w-full">
                <p className="font-extrabold w-full text-[15pt]">{item.title}</p>
                <p className="font-medium w-full text-[13pt]">{item.subtitle}</p>
                <p className="font-medium text-[#888888] text-[12pt] mt-1">{item.openDt.replaceAll("-",".")}</p>
                <div className="mt-1">
                    <span className="text-[#FFD177] text-[16pt]">★</span>
                    <span className="font-bold text-[14pt]"> {item.rating}</span>
                </div>
            </div>
            <div>
                {
                item.like
                ?<p className="cursor-pointer text-[#FF6F6F] text-[20pt]" onClick={()=>btnHeart(item)}>♥︎</p>
                :<p className="cursor-pointer text-[#888888] text-[20pt]" onClick={()=>btnHeart(item)}>♥︎</p>
                }
            </div>
        </div>
    </li>
  )
}
export default React.memo(List);
