import React from 'react'
import Comments from './Comments'
import Form from './Form'
import axios from 'axios'
;
export default function Favlist({item,userId}) {
    const btnHeart = (movieSeq) => {
        alert(`${item.movieNm}가 관심 목록에서 삭제되었습니다!`);

        axios.delete(`/api/v1/movie/`+item.movieSeq+`?userId=${userId}`).then(function(){
            window.location.reload();
        });

    }

  return (
    <li class="px-7 py-5 mb-[25px] bg-[#ffffff] rounded-[14px]">
        <div class="flex">

            <img src={item.thumbnail} alt="Err" class=" mr-5 float-left w-[85px]"></img>

            <div class="w-full">
                <p class="font-extrabold w-full text-[15pt]">{item.movieNm}</p>
                <p class="font-medium text-[#888888] text-[12pt] mt-1">{item.openDt}</p>
                <div class="mt-1">
                    <span class="font-medium text-[14pt]">전체</span>
                    <span class="text-[#FFD177] text-[16pt]">★</span>
                    <span class="font-medium text-[14pt]">{item.rating}</span>
                </div>
            </div>

            <div>
                <p class="cursor-pointer text-[#FF6F6F] text-[20pt]" onClick={()=>btnHeart(item)}>♥︎</p>
            </div>

        </div>
        <Comments item={item}></Comments>
        <Form userId={userId} item={item}></Form>
    </li>
  )
}
