import {React, useState} from 'react'

export default function Form({userId, item}) {

    const [rating,setRating] = useState(0);
    const [comment,setComment] = useState("");

    const inputComment = (e) => {
        setComment(e.target.value);
    }

    const btnStar = (value) =>{
        console.log(value);

        //setRating(e.target.value);
    }

    const btnReply = (e) =>{
        console.log(userId);
        console.log(rating);
        console.log(comment);
    }
    return (
    <div>
        <form class="group relative w-full">
            <div class="star-rating">
                <input type="radio" id={`5${item.movieCd}`} name="rating" value="5" onClick={()=>btnStar(this.value)}/>
                <label for="5-stars" class="star" >&#9733;</label>
                <input type="radio" id={`4${item.movieCd}`} name="rating" value="4" onClick={btnStar}/>
                <label for="4-stars" class="star">&#9733;</label>
                <input type="radio" id={`3${item.movieCd}`} name="rating" value="3" onClick={btnStar}/>
                <label for="3-stars" class="star">&#9733;</label>
                <input type="radio" id={`2${item.movieCd}`} name="rating" value="2" onClick={btnStar}/>
                <label for="2-stars" class="star">&#9733;</label>
                <input type="radio" id={`1${item.movieCd}`} name="rating" value="1" onClick={btnStar}/>
                <label for="1-star" class="star">&#9733;</label>
            </div>

            <div class="flex items-center justify-between">

                <input type='text' placeholder='한줄평을 입력해주세요'
                className="form-input w-full rounded-md border-[#D9D9D9] shadow-m
                font-medium text-[#000000] placeholder:font-medium placeholder:text-[#888888]
                focus:border-[#64A2BD] focus:ring focus:ring-[#64A2BD] focus:ring-opacity-50"
                onChange={inputComment}/>
                <button type="button"
                className="form-input hover:bg-[#598A9E] rounded-md bg-[#64A2BD] border-[#64A2BD] 
                text-white text-sm font-medium align-center ml-2 py-2.5 px-3 shadow-m"
                onClick={btnReply}>
                Reply
            </button>
            </div>     
        </form>         
    </div>
  )
}
