import React from 'react'
import Form from './Form';
import Comments from './Comments';
export default function List({item}) {
    const btnHeart = (title) => {

        const updatedata = data.map((item) => {
            if (item.title === title) {
                item.like = !item.like

            }
            return item;
        });

        setData(updatedata);
    }
  return (
    <li class="px-3 py-2">
                <div class="flex">
                    <img src={item.poster} alt="Err" class=" mr-5 float-left w-[85px]"></img>
                    <div class="w-full">
                        <p class="font-extrabold w-full text-[15pt]">{item.title}</p>
                        <p class="font-medium w-full text-[13pt]">{item.subtitle}</p>
                        <p class="font-medium text-[#888888] text-[12pt] mt-1">{item.openDt.replaceAll("-",".")}</p>
                        <div class="mt-1">
                            <span class="text-[#FFD177] text-[16pt]">★</span>
                            <span class="font-bold text-[14pt]"> {item.rating}</span>
                        </div>
                    </div>
                    <div>
                        {
                            item.like?<p class="text-[#FF6F6F] text-[20pt]" onClick={()=>btnHeart(item.title)}>♥︎</p>
                            :<p class="text-[#888888] text-[20pt]" onClick={()=>btnHeart(item.title)}>♥︎</p>
                        }

                    </div>
                </div>

                <Comments></Comments>

                <Form></Form>
            </li>
  )
}
