import React from 'react'
import Comments from './Comments'
import Form from './Form'
import axios from 'axios'
;
export default function Favlist({item,userId}) {
    const btnHeart = (movieSeq) => {
        alert(`${userId} : ${movieSeq}`);

        axios.delete(`api/v1/movie/${Seq}` + "/" + postId, {
            data: {
              // 서버에서 req.body.{} 로 확인할 수 있다.
              commentId: 1,
              userId: 0,
            },
            headers: {
              // 생략
            },
          })
    }

  return (
    <li class="px-7 py-5 mb-[25px] bg-[#ffffff] rounded-[14px]">
        <div class="flex">

            <img src={item.thumbnail} alt="Err" class=" mr-5 float-left w-[85px]"></img>

            <div class="w-full">
                <p class="font-extrabold w-full text-[15pt]">{item.movieNm}</p>
                <p class="font-medium w-full text-[13pt]">{item.audiAcc}</p>
                <p class="font-medium text-[#888888] text-[12pt] mt-1">{item.openDt}</p>
                <div class="mt-1">
                    <span class="text-[#FFD177] text-[16pt]">★</span>
                    <span class="font-bold text-[14pt]">{item.rating}</span>
                </div>
            </div>

            <div>
                <p class="cursor-pointer text-[#FF6F6F] text-[20pt]" onClick={()=>btnHeart(item.movieSeq)}>♥︎</p>
            </div>

        </div>
        <Comments ></Comments>
        <Form userId={userId}></Form>
    </li>
  )
}
