import axios from 'axios';
import { useEffect, useState } from 'react';
export default function Lists() {

    const KEY = 'ac733e4361119585d5f69a529226d016';
    const targetDate = '20220917';
    const ID_KEY = 'rkuFsJw7BejTzImF1dlV';
        const SECRET_KEY = 'vjda43t2Pd';
    const [data, setData] = useState('');
    const [movieList,setMovieList]= useState([  ]);
    let resultList = [];

    useEffect(()=>{
        axios.get(`http://www.kobis.or.kr/kobisopenapi/webservice/rest/boxoffice/searchDailyBoxOfficeList.json?key=${KEY}&targetDt=${targetDate}`)
        .then(res=>setData(res.data.boxOfficeResult.dailyBoxOfficeList))
    },[]);
    console.log(data);

    const posterImg = {

        url:'/v1/search/movie.json',
        method:'GET',
        params:{
            query:`한산:용의 `
        },
        headers: {
          'X-Naver-Client-Id': ID_KEY,
          'X-Naver-Client-Secret': SECRET_KEY
        },
        responseType:'json'

      }

      const getPosterImg = () =>{

        axios(posterImg)
        .then(function(response){
          let results = response.data.results;

          results.map((result)=>{
            let newList = {
              id: `${result.id}`,//유니크한 값을 표현하기 위해
              title: `${result.title}`,
              posterPath:`${result.poster_path}`,
              releaseDate:`${result.release_date}`,
              average:`${result.vote_average}`,
              popularity: `${result.popularity}`,
              language:`${result.original_language}`        
            }
            console.log(newList);
            resultList.push(newList);
          })

          console.log(resultList);


        })
        .catch(function(){
          alert('Fail to Loading..');
        })
        .finally(function(){
          alert('Done!');
          console.log(movieList);

          console.log(resultList);
          setMovieList(resultList); 
          // movieList.map((movie)=>{
          //   console.log(movie.title);
          // })
        })
      }
