    <div class="px-[40px] pt-[25px] grid grid-cols-5 gap-10">

        {
            data.map((movie)=>(
                <div class="w-fit">
                    <img src="https://ssl.pstatic.net/imgmovie/mdi/mit110/1941/194196_P130_180925.jpg"></img>
                    <p>{movie.movieNm}</p>
                    <p>{movie.openDt}</p>
                    <p>★ 8.5</p>
                </div>
            ))
        }

    </div>
  )
}
