import { useEffect,useState, React  } from 'react';
import axios from 'axios';


export default function Lists() {
    const KEY = 'ac733e4361119585d5f69a529226d016';
    const yesterday = new Date((new Date())-86400000);
    let targetDT = yesterday.getFullYear()+(yesterday.getMonth()+1).toString().padStart(2,0)+(yesterday.getDate()).toString().padStart(2,0);
    const[data,setData]=useState([]);

    var myHeaders = new Headers();
    myHeaders.append("X-Naver-Client-Id", "rkuFsJw7BejTzImF1dlV");
    myHeaders.append("X-Naver-Client-Secret", "vjda43t2Pd");

    var requestOptions = {
        method: 'GET',
        headers: myHeaders,
        redirect: 'follow'
    };

    useEffect(() => {
        const getMovies = async(movie) => {
            const json = await (
                await fetch(`/v1/search/movie.json?query=${movie.movieNm}`, requestOptions)
            ).json();
            return {
                "rank":movie.rank,
                "poster":json.items[0].image,
                "title":movie.movieNm,
                "openDt":movie.openDt,
                "rating":json.items[0].userRating   
            }
        }
        const getBoxOffice = async() => {
            const response = await(await (
                await fetch(
                    `https://kobis.or.kr/kobisopenapi/webservice/rest/boxoffice/searchDailyBoxOfficeList.json?key=${KEY}&targetDt=${targetDT}`
                )
            ).json()).boxOfficeResult.dailyBoxOfficeList;
            //console.log(response);
            //console.log(data);
            const boxOffice = response.map((movie) => getMovies(movie));

            await Promise.all(boxOffice).then((result) => {
                //console.log(result);
                setData(result);
            });
        }
        getBoxOffice();
    }, [targetDT]);



    return (
    <div class="px-[40px] pt-[25px] grid grid-cols-5 gap-10">
        {data.map((item)=>(
                <div class="w-fit">
                    <img src={item.poster} alt="Err"></img>
                    <p class="font-bold">{item.title}</p>
                    <p class="font-bold">{item.openDt.replaceAll("-",".")}</p>
                    <p class="font-bold">â˜… {item.rating}</p>
                </div>
        ))}


    </div>
    )
}
