import axios from 'axios';
import {React, useState} from 'react'
import {Link} from 'react-router-dom'

export default function Login() {
    const [id,setId]= useState('');
    const [pw,setPw]= useState('');
    const [user,setUser]= useState({});

    const inputId=(e)=>{
        setId(e.target.value);
    }

    const inputPw=(e)=>{
        setPw(e.target.value);        
    }

    const btnLogin = () => {
        // alert(`{id:${id},pw:${pw}}`);
        axios.get('/api/v1/user', {
            userId : `${id}`,
            password : `${pw}`
        })
        .then(function (response) {
             console.log(response.data);
             let userDataList = response.data;
             userDataList.map((userData)=>{
                if(userData.userId ===`${id}` | userData.password ===`${pw}`){
                    setUser(userData)
                }
             })

        }).catch(function (error) {
            alert('Fail to Signup');
            console.log(error);  
        }).then(function() {
            //always
        });
    }
  return (
  <div class=" bg-[url('./images/loginBg.png')] bg-full bg-center w-full h-screen items-center justify-center ">


            <form class="py-[15rem] px-[20rem]">
            <label class="block my-8">
                <input type='text' placeholder='ID'
                class="form-input block w-full py-3 rounded-md border-[#D9D9D9] shadow-m
                focus:border-[#64A2BD] focus:ring focus:ring-[#64A2BD] focus:ring-opacity-50 placeholder:font-medium"                
                onChange={inputId}/>

            </label>

            <label class="block my-8">
                <input type='password' placeholder='PW'
                class="form-input block w-full py-3 rounded-md border-[#D9D9D9] shadow-m
                focus:border-[#64A2BD] focus:ring focus:ring-[#64A2BD] focus:ring-opacity-50 placeholder:font-medium"
                onChange={inputPw}/>
            </label>
            <Link to="/" class="no-underline">
            <button type="button" class="bg-[#FFD177] w-full rounded-md shadow-sm px-4 py-3
            text-white text-[18px] font-medium text-center"
            onClick={btnLogin}>
                Login
            </button>
            </Link>

            <Link to="/signup" class="no-underline"  state={{ user: user}}>
                <p class="text-[#FFD177] text-[18px] underline font-medium text-center my-5">
                    Signup
                </p>
            </Link>

        </form>
    </div>

  )
}
