import { useEffect,useState, React  } from 'react';
import axios from 'axios';


export default function Lists() {
    const KEY = 'ac733e4361119585d5f69a529226d016';
    const targetDate = '20220917';
    const[data,setData]=useState([]);

    const getPosterImg = (title) =>{
        const posterImg = {
            url:'/v1/search/movie.json',
            method:'GET',
            params:{
                query: `${title}`
            },
            headers: {
                'X-Naver-Client-Id': "rkuFsJw7BejTzImF1dlV",
                'X-Naver-Client-Secret': "vjda43t2Pd"
            }
        }
        axios(posterImg)
        .then(function(response){
            let results = response.data.items[0].image;
            return results;   
        })
        .catch(function(){
            console.log('Fail to Loading..');
        })
        .finally(function(){
            console.log('Done!');

        })
      }


  useEffect(() => {
    const getMovies = async () => {
      try {
        const response = await axios.get(
          `http://www.kobis.or.kr/kobisopenapi/webservice/rest/boxoffice/searchDailyBoxOfficeList.json?key=${KEY}&targetDt=${targetDate}`
        );
        console.log(response.data.boxOfficeResult.dailyBoxOfficeList);
        setData(response.data.boxOfficeResult.dailyBoxOfficeList);
      } catch (e) {
        console.log(e);
      }
    };
    getMovies();
  }, []);

    return (
    <div class="px-[40px] pt-[25px] grid grid-cols-5 gap-10">
        {data.map((item)=>(
                <div class="w-fit">
                    <img src={`$getPosterImg(item.movieNm)}`} alt=""></img>
                    <p >{item.movieNm}</p>
                    <p>{item.openDt}</p>
