import { useEffect,useState, React  } from 'react';
import axios from 'axios';


export default function Lists() {
    const KEY = 'ac733e4361119585d5f69a529226d016';
    const yesterday = new Date((new Date())-86400000);
    let targetDT = yesterday.getFullYear()+(yesterday.getMonth()+1).toString().padStart(2,0)+(yesterday.getDate()).toString().padStart(2,0);
    const[data,setData]=useState([]);

    var myHeaders = new Headers();
    myHeaders.append("X-Naver-Client-Id", "rkuFsJw7BejTzImF1dlV");
    myHeaders.append("X-Naver-Client-Secret", "vjda43t2Pd");

    var requestOptions = {
        method: 'GET',
        headers: myHeaders,
        redirect: 'follow'
    };

    useEffect(() => {
        const getMovies = async(movie) => {
            const json = await (
                await fetch(`/v1/search/movie.json?query=${movie.movieNm}`, requestOptions)
            ).json();
            return {
                "rank":movie.rank,
                "poster":json.items[0].image,
                "title":movie.movieNm,
                "subtitle":json.items[0].subtitle,   
                "openDt":movie.openDt,
                "rating":json.items[0].userRating   
            }
        }
        const getBoxOffice = async() => {
            const response = await(await (
                await fetch(
                    `https://kobis.or.kr/kobisopenapi/webservice/rest/boxoffice/searchDailyBoxOfficeList.json?key=${KEY}&targetDt=${targetDT}`
                )
            ).json()).boxOfficeResult.dailyBoxOfficeList;
            //console.log(response);
            //console.log(data);
            const boxOffice = response.map((movie) => getMovies(movie));

            await Promise.all(boxOffice).then((result) => {
                //console.log(result);
                setData(result);
            });
        }
        getBoxOffice();
    }, [targetDT]);

    return (
    <div class="px-[40px] pt-[25px]">
        <ul>
            {data.map((item)=>(
            <li class="px-3 py-2">
                <div class="flex">
                    <img src={item.poster} alt="Err" class=" mr-5 float-left w-[90px]"></img>
                    <div>
                        <p class="font-extrabold w-full text-[16pt]">{item.title}</p>
                        <p class="font-medium w-full text-[12pt] mt-2">{item.subtitle}</p>
                        <p class="font-medium text-[#888888] text-[10pt] mt-2">{item.openDt.replaceAll("-",".")}</p>
                        <span class="text-[#FFD177] text-[16pt] mt-3">â˜…</span><span class="font-bold text-[14pt] mt-3"> {item.rating}</span>
                    </div>
                </div>
                <div class="flex items-center">
                <div class="flex items-center">
                    <img src={require('./image/profile_sample.png')} class="w-7 h-7 mr-3" alt="Default"></img>
                    <span class="font-[450] text-[16px] align-center ">{answer.username}</span>
                </div>
                </div>
                <div>

                </div>
            </li>
            ))}
        </ul>
    </div>
    )
}
