            userId: "",
            movieCd:""
        })
        .then(function (response) {
             console.log(response);  
        }).catch(function (error) {
            alert('Fail to Signup');
        }).then(function() {
            //always
        });
    }
    return (
    <li className="px-7 py-5 mb-[25px] bg-[#ffffff] rounded-[14px]">
        <div className="flex">
            <img src={item.poster} alt="Err" className=" mr-5 float-left w-[85px]"></img>
            <div className="w-full">
                <p className="font-extrabold w-full text-[15pt]">{item.title}</p>
                <p className="font-medium w-full text-[13pt]">{item.subtitle}</p>
                <p className="font-medium text-[#888888] text-[12pt] mt-1">{item.openDt.replaceAll("-",".")}</p>
                <div className="mt-1">
                    <span className="text-[#FFD177] text-[16pt]">★</span>
                    <span className="font-bold text-[14pt]"> {item.rating}</span>
                </div>
            </div>
            <div>
                {
                item.like
                ?<p className="cursor-pointer text-[#FF6F6F] text-[20pt]" onClick={()=>btnHeart(item.title)}>♥︎</p>
                :<p className="cursor-pointer text-[#888888] text-[20pt]" onClick={()=>btnHeart(item.title)}>♥︎</p>
                }
            </div>
        </div>
    </li>
  )
}
export default React.memo(List);
