import { useEffect,useState, React  } from 'react';
import Form from './Form';


export default function Lists() {
    const KEY = 'ac733e4361119585d5f69a529226d016';
    const yesterday = new Date((new Date())-86400000);
    let targetDT = yesterday.getFullYear()+(yesterday.getMonth()+1).toString().padStart(2,0)+(yesterday.getDate()).toString().padStart(2,0);
    const[data,setData]=useState([]);

    const btnHeart = (id) => {

        console.log(id);
        console.log(data[id].like);
        data[id].like = !(data[id].like);
        // const updatedata = data.map((item) => {
        //     if (item.id === id) {
        //         //item.like = !item.like


        //     return item;
        // });

        //setData(updatedata);
    }

    useEffect(() => {
        const getMovies = async(movie) => {
            const json = await (
                await fetch(`/v1/search/movie.json?query=${movie.movieNm}`, {
                    method: 'GET',
                    headers: {
                        "X-Naver-Client-Id":"rkuFsJw7BejTzImF1dlV",
                        "X-Naver-Client-Secret":"vjda43t2Pd"
                    }
                })
            ).json();
            return {
                "rank":movie.rank,
                "poster":json.items[0].image,
                "title":movie.movieNm,
                "subtitle":json.items[0].subtitle,   
                "openDt":movie.openDt,
                "rating":json.items[0].userRating,
                "like":false   
            }
        }
        const getBoxOffice = async() => {
            const response = await(await (
                await fetch(
                    `https://kobis.or.kr/kobisopenapi/webservice/rest/boxoffice/searchDailyBoxOfficeList.json?key=${KEY}&targetDt=${targetDT}`
                )
            ).json()).boxOfficeResult.dailyBoxOfficeList;
            //console.log(response);
            //console.log(data);
            const boxOffice = response.map((movie) => getMovies(movie));

            await Promise.all(boxOffice).then((result) => {
                //console.log(result);
                setData(result);
            });
        }
        getBoxOffice();
    }, [targetDT]);



    return (
    <div class="px-[40px] pt-[25px]">
        <ul>
            {data.map((item,id)=>(
            <li class="px-3 py-2">
                <div class="flex">
                    <img src={item.poster} alt="Err" class=" mr-5 float-left w-[85px]"></img>
                    <div class="w-full">
                        <p class="font-extrabold w-full text-[15pt]">{item.title}</p>
                        <p class="font-medium w-full text-[13pt]">{item.subtitle}</p>
                        <p class="font-medium text-[#888888] text-[12pt] mt-1">{item.openDt.replaceAll("-",".")}</p>
                        <div class="mt-1">
                            <span class="text-[#FFD177] text-[16pt]">★</span>
                            <span class="font-bold text-[14pt]"> {item.rating}</span>
                        </div>
                    </div>
                    <div>
                        <p class={item.like ? `text-[#FF6F6F]` : `${"text-[#888888]"}`} onClick={()=>btnHeart(id)}>♥︎</p>

                    </div>
                </div>

                <div>
                    <ul>
                        <li>
                            <div class="flex items-center mt-3">
                                <img src={require('./images/profile_sample.png')} class="w-7 h-7 mr-3" alt="Default"></img>
                                <span class="font-[450] text-[16px] align-center ">최고에요</span>
                            </div>
                        </li>
                        <li>
                            <div class="flex items-center mt-3">
                                <img src={require('./images/profile_sample.png')} class="w-7 h-7 mr-3" alt="Default"></img>
                                <span class="font-[450] text-[16px] align-center ">최고에요</span>
                            </div>
                        </li>
                    </ul>
                </div>

                <Form></Form>
            </li>
            ))}
        </ul>
    </div>
    )
}
