import axios from 'axios';


export default function Lists() {
    const KEY = 'ac733e4361119585d5f69a529226d016';
  const targetDate = '20220917';
  const[data,setData]=useState([]);

  getSearchMovie = async () => {
    const ID_KEY = 'id_key';
    const SECRET_KEY = 'secret_key';
    const search = this.state.value;
    try {
      if (search === "") {
        this.setState({movies: [], isLoading: false})
      } else {
        const {data: {
            items
          }} = await axios.get('v1/search/movie.json',{
            params:{
              query: search,
              display: 20
            },
            headers: {
              'X-Naver-Client-Id': ID_KEY,
              'X-Naver-Client-Secret': SECRET_KEY
            }
          });

        this.setState({movies: items, isLoading: false});
      }
    } catch (error) {
      console.log(error);
    }
  };


  useEffect(() => {
    const getMovies = async () => {
      try {
        const response = await axios.get(
          `http://www.kobis.or.kr/kobisopenapi/webservice/rest/boxoffice/searchDailyBoxOfficeList.json?key=${KEY}&targetDt=${targetDate}`
        );
        console.log(response.data.boxOfficeResult.dailyBoxOfficeList);
        setData(response.data.boxOfficeResult.dailyBoxOfficeList);
      } catch (e) {
        console.log(e);
      }
    };
    getMovies();
  }, []);

    return (
    <div class="px-[40px] pt-[25px] grid grid-cols-5 gap-10">
