  //         "subtitle":json.items[0].subtitle,   
  //         "openDt":movie.openDt.replaceAll("-","."),
  //         "audiAcc":movie.audiAcc,
  //         "rating":json.items[0].userRating,
  //         "like":false   
  //     }
  // }
  const btnSearch = (e) => {
    const getMovies = async(movie) => {
      const json = await (
          await fetch(`/v1/search/movie.json?query=${movie.movieNm}`, {
              method: 'GET',
              headers: {
                  "X-Naver-Client-Id":"rkuFsJw7BejTzImF1dlV",
                  "X-Naver-Client-Secret":"vjda43t2Pd"
              }
          })
      ).json();
      return {
          "rank":movie.rank,
          "poster":json.items[0].image,
          "movieCd":movie.movieCd,
          "title":movie.movieNm,
          "subtitle":json.items[0].subtitle,   
          "openDt":movie.openDt.replaceAll("-","."),
          "audiAcc":movie.audiAcc,
          "rating":json.items[0].userRating,
          "like":false   
      }
  }
  const getBoxOffice = async() => {
      const response = await(await (
          await fetch(
            `https://kobis.or.kr/kobisopenapi/webservice/rest/movie/searchMovieList.json?key=${KEY}&movieNm=${search}`
          )
      ).json()).movieListResult.movieList;
      console.log(response);
      //console.log(data);
      const boxOffice = response.map((movie) => getMovies(movie));

      await Promise.all(boxOffice).then((result) => {
          console.log(result);
          // setData(result);
      });
      getBoxOffice();
  }


  }

  return (
    <div className="flex flex-row px-[10rem] py-[15px]  items-center">
      <Link to="/" className="no-underline" state={user}>
        <span className="cursor-pointer text-[22pt] font-extrabold w-fit text-[#FFD177]">FavieList</span>
      </Link>


      <div className="w-full">
        <form className="mx-5">

          <input className="form-input inline-block
          py-2 pl-10 w-fit
          rounded-md border-[#D9D9D9] shadow-m font-medium text-[#000000]
          focus:border-[#64A2BD] focus:ring focus:ring-[#64A2BD] focus:ring-opacity-50
          placeholder:font-medium placeholder:text-[#888888]"
          type="text" placeholder="Search"
          onChange={inputSearch}/>
           <button type="button"
                className="form-input hover:bg-[#598A9E] rounded-md bg-[#64A2BD] border-[#64A2BD] 
                text-white text-sm font-medium align-center ml-2 py-2.5 px-3 shadow-m"
                onClick={btnSearch}>
                Reply
            </button>
